import React from 'react';
import {Component} from 'react';
import './Location.css';
import './w3.css'
import rickyandmorky from './rickyandmorky.jpeg';
import { FaSearch } from 'react-icons/fa';


/* The project is designed to be in one component.The reason for that is  when  i used to implement the
functions or classes in multiple components it is very hard to pass data from one component to other components
although i use props and state components. We need to send as well as receive data from each component to many other 
component. I know it is possible to make but the most of the concepts are not covered in class and the project deadline is near
so that why i unable to implement the project in multiple components 
The following functions are not implemented due to above reasons:

1. Page loading spinner.
2. Content font family and some stylings.
3. multiple component classes or functions.

please forgive us this as the most topics are not covered in class session.  

*/



class Location extends Component
 {

   constructor(props)
    {
      super(props);
     this.state=
   {



       list:       //locations fetch
       {
          results:[]
       }

       ,
       lists:{},//location data fetch
       Names:{}, 
       error:null,
       input:'',
       count:1,


      showMe:false,//Location boxes
      showUs:false,//location data
      showLoc:true,//Location tag
      showHome:true,//Homepage
      showHometag:false,//Home tag
     }
   }








   Handler = (f) => {                                 // panel onclick handler

    this.setState({showHome:false})
   this.setState({id:f});
   this.setState({showMe:false});
   this.setState({showUs:true});
   this.setState({showLoc:true});
   this.setState({showHometag:true});
   this.setState({count:1});

   this.componentMount(f);




   }

componentMount(f)                                            //Get location data function
{


    let url="https://rickandmortyapi.com/api/location/"+f;         // location data fetch api



    fetch(url)
    .then((resp) => resp.json())
    .then(json => {
     this.setState({lists: json})

       this.componentDidCatch(this.state.lists.residents);

       document.getElementById("searchbox").value=this.state.lists.name;
    })


    .catch(function(error) {
      console.log('Request failed', error);
    });





  }


  componentDidCatch(v)                                           //Get Location data residents names function
  {


    for (let i in v)
    {


       let urll=v[i]; 
       fetch(urll)
       .then((respp) => respp.json())
       .then(json => {
        this.setState({Names: json})
        this.component(this.state.Names.name, i,v);

       })


       .catch(function(error) {
         console.log('Request failed', error);
       });

  }
  }


  component(e)                                                    // update location data residents list function 
  {
    let c=this.state.count;
    document.getElementById("residents").innerHTML +=   c+ ".<b id='resinames'> " +e+"</b><br/>";
    this.setState({count :c+1});
  }

















  redirect = () => {                     // location tag onclick handler

this.setState({showMe:true});
this.setState({showUs:false});
this.setState({showLoc:true});
this.setState({showHome:false})
this.setState({showHometag:true}); 
this.setState({count:1});
document.getElementById("searchbox").value="";
document.getElementById("searchbox").placeholder="Type a location";

  }


  homeredirect = () =>                                           //Home tag onclick handler function
  { 
    this.setState({showHome:true});

    this.setState({showMe:false});
    this.setState({showUs:false});
    this.setState({count:1});
    document.getElementById("searchbox").value="";
     document.getElementById("searchbox").placeholder="Type a location";
  }







   componentDidMount()                 //locations fetch api
   {
     let url="https://rickandmortyapi.com/api/location/";


   fetch(url)
   .then((resp) => resp.json())
   .then(json => {
    this.setState({list: json})



   })

   .catch(function(error) {
     console.log('Request failed', error);
   });


  }












  render()
 {

let res= this.state.list.results;           // locations fetch to variable res
let r=this.state.lists;                     //location data fetch to r
                                                //declare and assign date to variable
let date=require('dateformat');             







